import { Component } from '@angular/core';

@Component({
  selector: 'app-footer',
  templateUrl: './footer.component.html',
  styleUrls: ['./footer.component.scss']
})
export class FooterComponent {
  scrollUp() {
    window.scrollTo(0, 0);
  }

}
<div class="contentFooter">
    <!-- <span>Footer</span>
    <a routerLink="/imprint">Imprint</a> -->
    <div class="logoAndImprintContainer">
        <a href="#"><img class="logoImage" src="./../../assets/img/j-development-logo NEU.png"></a>
        <a class="imprintLink" routerLink="/imprint" (click)="scrollUp()">Impressum</a>        
    </div>
    <!-- <img class="logoImage" src="./../../assets/img/j-development-logo.png"> -->
    <a href="#about-me" class="copyrightText">&#xA9;2023 Joachim Dürr</a>
    <div class="socialMedia">
        <a href="https://github.com/Yehoyakim88/"><img class="socialMediaImages" src="./../../assets/img/GitHub Link.png"></a>
        <a href="mailto:jk.duerr@t-online.de"><img class="socialMediaImages" src="./../../assets/img/Mail Icon.png"></a>
        <a href=""><img class="socialMediaImages" src="./../../assets/img/LinkedIn Link.png"></a>
    </div>
</div>
